CREATE OR REPLACE PROCEDURE inativar_horario_agenda (cd_tipo_agenda_p bigint, cd_agenda_p bigint, nr_seq_agenda_p bigint, cd_motivo_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE

 
hr_inicio_w	timestamp;


BEGIN 
if (cd_tipo_agenda_p IS NOT NULL AND cd_tipo_agenda_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
 
	if (cd_tipo_agenda_p = 2) then -- agenda de exames 
		 
		/* inativar horário */
 
		update	agenda_paciente 
		set	ie_status_agenda	= 'II', 
			nr_seq_mot_inativ	= cd_motivo_p, 
			ds_mot_inativ	= ds_motivo_p, 
			nm_usuario_inativ	= nm_usuario_p, 
			dt_inativo		= clock_timestamp() 
		where	cd_agenda	= cd_agenda_p 
		and	nr_sequencia	= nr_seq_agenda_p;
 
		/* obter horário */
 
		select	hr_inicio 
		into STRICT	hr_inicio_w 
		from	agenda_paciente 
		where	nr_sequencia = nr_seq_agenda_p;
		 
		/* gerar histórico */
 
		CALL gerar_agenda_paciente_hist(cd_agenda_p,nr_seq_agenda_p,'I',nm_usuario_p,obter_desc_expressao(489864)/*'Inativação do horário '*/ || to_char(hr_inicio_w,'dd/mm/yyyy hh24:mi:ss')||obter_desc_expressao(327458)/*' (Motivo: '*/||coalesce(obter_desc_agenda_motivo(cd_motivo_p),obter_desc_expressao(327119)/*'Não informado'*/)||' '||ds_motivo_p||')',null,null,hr_inicio_w, obter_perfil_ativo);
 
	elsif (cd_tipo_agenda_p in (3,4)) then -- agenda de consultas 
		 
		/* inativar horário */
 
		update	agenda_consulta 
		set	ie_status_agenda	= 'II', 
			nm_usuario_status	= nm_usuario_p, 
			nr_seq_motivo_transf	= cd_motivo_p, 
			ds_motivo_status		= ds_motivo_p, 
			dt_status			= clock_timestamp() 
		where	nr_sequencia		= nr_seq_agenda_p;
 
		/* obter horário */
 
		select	dt_agenda 
		into STRICT	hr_inicio_w 
		from	agenda_consulta 
		where	nr_sequencia = nr_seq_agenda_p;
		 
		/* gerar histórico */
 
		CALL gerar_agenda_consulta_hist(cd_agenda_p,nr_seq_agenda_p,'I',nm_usuario_p,obter_desc_expressao(489864)/*'Inativação do horário '*/ || to_char(hr_inicio_w,'dd/mm/yyyy hh24:mi:ss'),null,null,hr_inicio_w);
 
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
