CREATE OR REPLACE PROCEDURE inativar_item_cot_compra ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w				bigint;


BEGIN

update	cot_compra_item
set	ie_situacao = 'I'
where	nr_cot_compra = nr_cot_compra_p
and	nr_item_cot_compra = nr_item_cot_compra_p;

select	count(*)
into STRICT	qt_existe_w
from	cot_compra_forn_item
where	nr_cot_compra = nr_cot_compra_p
and	nr_item_cot_compra = nr_item_cot_compra_p;

if (qt_existe_w > 0) then
	update	cot_compra_forn_item
	set	ie_situacao = 'I'
	where	nr_cot_compra = nr_cot_compra_p
	and	nr_item_cot_compra = nr_item_cot_compra_p;
end if;

CALL gerar_historico_cotacao(nr_cot_compra_p,WHEB_MENSAGEM_PCK.get_texto(302772),WHEB_MENSAGEM_PCK.get_texto(302774,'NR_ITEM_COT_COMPRA_P=' || nr_item_cot_compra_p),'S',nm_usuario_p);



commit;

end;
