CREATE OR REPLACE PROCEDURE inativar_lote_fornec_est_nf ( nr_seq_lote_p bigint, nr_seq_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_nota_fiscal_w			varchar(255);
ds_historico_w			varchar(4000);

BEGIN

select	nr_nota_fiscal
into STRICT	nr_nota_fiscal_w
from	nota_fiscal
where	nr_sequencia = nr_seq_nf_p;

update	material_lote_fornec
set	ie_situacao = 'I'
where	nr_Sequencia = nr_seq_lote_p;

ds_historico_w	:= substr(wheb_mensagem_pck.get_texto(313984,'NR_NOTA_FISCAL=' || nr_nota_fiscal_w ||
			';NR_SEQ_NF=' || nr_seq_nf_p),1,255);

CALL sup_gravar_hist_lote_fornec(
	nr_seq_lote_p,
	substr(wheb_mensagem_pck.get_texto(313983),1,255),
	ds_historico_w,
	'S',
	nm_usuario_p);


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
