SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inativar_lote_fornec_zerado ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_historico_w	varchar(4000);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	material_lote_fornec
	where	cd_estabelecimento = cd_estabelecimento_p
	and	ie_situacao <> 'I'
	and	obter_saldo_lote_fornec(nr_sequencia) <= 0;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	material_lote_fornec
	set	ie_situacao = 'I',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_sequencia_w;

	ds_historico_w	:= substr(wheb_mensagem_pck.get_texto(313985),1,255);

	CALL sup_gravar_hist_lote_fornec(
		nr_sequencia_w,
		substr(wheb_mensagem_pck.get_texto(313983),1,255),
		ds_historico_w,
		'S',
		nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
