SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inativar_material_kit (cd_material_p bigint, ie_tipo_kit_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_restringe_estab_w	varchar(1);


BEGIN

ie_restringe_estab_w := Obter_Param_Usuario(143, 281, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_restringe_estab_w);

update	componente_kit a
set	a.ie_situacao	=	'I',
	a.ds_observacao	=	substr(wheb_mensagem_pck.get_texto(313986,'NM_USUARIO=' || nm_usuario_p ||
						';DT_INATIVACAO=' || to_char(trunc(clock_timestamp()),'dd/mm/yyyy')),1,255)
where	a.cd_material = cd_material_p
and (ie_restringe_estab_w <> 'E' or (SELECT coalesce(cd_estab_exclusivo,cd_estabelecimento_p) from kit_material where cd_kit_material = a.cd_kit_material) = cd_estabelecimento_p)
and	((coalesce(ie_tipo_kit_p,'0') = '0') or (coalesce(ie_tipo_kit_p,'0') <> '0' and exists ( select 	1
										from 	kit_material b
										where 	b.cd_kit_material = a.cd_kit_material
										and	b.ie_tipo = ie_tipo_kit_p)));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inativar_material_kit (cd_material_p bigint, ie_tipo_kit_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

