CREATE OR REPLACE PROCEDURE inativar_pertence_exame (nr_sequencia_p bigint, ds_tipo_p text, nm_usuario_p text) AS $body$
DECLARE

/* ds_tipo_p  	Exame ou Pertence	   */

BEGIN
if (ds_tipo_p = 'Exame')  then
	update exame_paciente
	set ie_situacao = 'I',
           	      nm_usuario = nm_usuario_p,
	      dt_atualizacao = clock_timestamp()
	where   nr_sequencia	=	nr_sequencia_p;
elsif (ds_tipo_p = 'Pertence') then
      begin
      update pertence_paciente
      set ie_situacao = 'I',
	      nm_usuario = nm_usuario_p,
	      dt_atualizacao = clock_timestamp()
      where   nr_sequencia	=	nr_sequencia_p;
      end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
