CREATE OR REPLACE PROCEDURE inativar_pe_prescricao ( nm_usuario_p text, ds_justificativa_p text, nr_sequencia_p bigint) AS $body$
BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	begin 
	update	pe_prescricao 
	set 	dt_inativacao = clock_timestamp(), 
		ie_situacao = 'I', 
		nm_usuario_inativacao = nm_usuario_p, 
		ds_justificativa = ds_justificativa_p 
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;
