CREATE OR REPLACE PROCEDURE inativar_protocolo_onco ( nr_seq_paciente_p bigint, ie_inativar_ant_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE

 
nr_seq_paciente_w		bigint;
cd_pessoa_fisica_w	varchar(10);
cd_protocolo_w		bigint;
nr_seq_medicacao_w	integer;
ds_mensagem_w		varchar(32000);	
 

BEGIN 
 
 
select	max(cd_protocolo), 
	max(nr_seq_medicacao), 
	max(cd_pessoa_fisica) 
into STRICT	cd_protocolo_w, 
	nr_seq_medicacao_w, 
	cd_pessoa_fisica_w 
from	paciente_setor 
where	nr_seq_paciente	= nr_seq_paciente_p;
 
if (ie_inativar_ant_p = 'S') then 
	 
	update	paciente_setor 
	set	ie_status = 'I' 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w 
	and	ie_status <> 'F'	 
	and	nr_seq_paciente	<> nr_seq_paciente_p;
 
elsif (ie_inativar_ant_p = 'M') and (cd_protocolo_w IS NOT NULL AND cd_protocolo_w::text <> '') and (nr_seq_medicacao_w IS NOT NULL AND nr_seq_medicacao_w::text <> '') then 
         
	update	paciente_setor 
	set	ie_status = 'I' 
	where	cd_pessoa_fisica 	= cd_pessoa_fisica_w 
	and	nr_seq_paciente 	<> nr_seq_paciente_p 
	and	ie_status <> 'F' 
	and	nr_seq_medicacao 	= nr_seq_medicacao_w 
	and	cd_protocolo 	= cd_protocolo_w;
 
elsif ( ie_inativar_ant_p = 'T') then 
 
	ds_mensagem_w := Consistir_inativar_protocolo(cd_pessoa_fisica_w, nr_seq_paciente_p, 'I', ds_mensagem_w);
 
elsif (ie_inativar_ant_p = 'Q') then 
	ds_mensagem_w := Consistir_inativar_protocolo(cd_pessoa_fisica_w, nr_seq_paciente_p, 'VR', ds_mensagem_w);
	if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then	 
		ds_mensagem_p := substr(ds_mensagem_w,1,255);
	end if;
end if;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
