CREATE OR REPLACE PROCEDURE inativar_template_sinal_vital ( nr_sequencia_p bigint, nm_usuario_p text, ds_justificativa_p text default null) AS $body$
DECLARE


nr_seq_reg_elemento_w	bigint;
nm_tabela_w		varchar(50);

c01 CURSOR FOR
	SELECT	d.nr_sequencia,
		e.nm_tabela
	from 	ehr_registro b,
		ehr_reg_template c,
		ehr_reg_elemento d,
		ehr_template_cont_atrib e
	where 	b.nr_sequencia = c.nr_seq_reg
	and	b.nr_sequencia = nr_sequencia_p
	and	c.nr_sequencia = d.nr_seq_reg_template
	and	d.nr_seq_temp_conteudo = e.nr_seq_temp_conteudo
	and ((d.ds_resultado IS NOT NULL AND d.ds_resultado::text <> '') or
		  (d.dt_resultado IS NOT NULL AND d.dt_resultado::text <> '') or
		  (d.vl_resultado IS NOT NULL AND d.vl_resultado::text <> ''));


BEGIN
open C01;
loop
fetch c01 into
	nr_seq_reg_elemento_w,
	nm_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nm_tabela_w = 'ATENDIMENTO_SINAL_VITAL') then
		update	atendimento_sinal_vital
		set	dt_inativacao	      = clock_timestamp(),
			nm_usuario_inativacao = nm_usuario_p,
			ie_situacao	      = 'I',
			ds_justificativa      = ds_justificativa_p
		where	nr_seq_reg_elemento   = nr_seq_reg_elemento_w;
	elsif (nm_tabela_w = 'ATENDIMENTO_MONIT_RESP') then
		update	atendimento_monit_resp
		set	dt_inativacao	      = clock_timestamp(),
			nm_usuario_inativacao = nm_usuario_p,
			ie_situacao	      = 'I',
			ds_justificativa      = ds_justificativa_p
		where	nr_seq_reg_elemento   = nr_seq_reg_elemento_w;
	end if;
	end;
end loop;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
