CREATE OR REPLACE PROCEDURE inativa_item_pertence (nr_sequencia_p bigint, nm_usuario_p text, nr_seq_item_p bigint, Ie_tipo_inativacao_p text) AS $body$
DECLARE

        
        nr_seq_armario_w		    pertence_paciente.nr_seq_armario%type;
        ie_param_29             varchar(1);

BEGIN

if (Ie_tipo_inativacao_p = 'P') then
		Update 	Pertence_paciente_item
		set	ie_situacao = 'I',
			dt_inativacao = clock_timestamp(),
			nm_usuario_inativacao = nm_usuario_p
		where 	nr_seq_pertence_paciente = nr_sequencia_p;

    ie_param_29 := obter_param_usuario(991, 29, obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_param_29);
    if ie_param_29 = 'S' then
       select max(nr_seq_armario) into STRICT nr_seq_armario_w from pertence_paciente where	nr_sequencia = nr_sequencia_p;
       update armario_pertence set IE_LIVRE = 'S' where nr_sequencia = nr_seq_armario_w;
    end if;
		
elsif (Ie_tipo_inativacao_p = 'I') then
	Update 	Pertence_paciente_item
	set	ie_situacao = 'I',
		dt_inativacao = clock_timestamp(),
		nm_usuario_inativacao = nm_usuario_p
	where 	nr_sequencia = nr_seq_item_p;
end if;
commit;

end;
