CREATE OR REPLACE PROCEDURE inativa_resultado_exame ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_motivo_result_p bigint, ie_opcao_p text, ds_observacao_p text, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_result_laborat_w		bigint;
ie_status_atend_w		smallint;


BEGIN

select	max(ie_status_atend)
into STRICT	ie_status_atend_w
from	prescr_procedimento
where	nr_prescricao 	= nr_prescricao_p
and	nr_sequencia 	= nr_seq_prescr_p;

if (ie_status_atend_w < 35) then
	--'O status do exame deve estar aprovado para que seja inativado o resultado!
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264057);
end if;

select	max(nr_sequencia)
into STRICT	nr_seq_result_laborat_w
from	result_laboratorio
where	nr_prescricao  = nr_prescricao_p
and	nr_seq_prescricao = nr_seq_prescr_p;

if (nr_seq_result_laborat_w IS NOT NULL AND nr_seq_result_laborat_w::text <> '') then

	insert into result_lab_hist_ativ(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_opcao,
		ds_observacao,
		nr_seq_result_laborat,
		nr_seq_motivo_result
		)
	values (
		nextval('result_lab_hist_ativ_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ie_opcao_p,
		ds_observacao_p,
		nr_seq_result_laborat_w,
		nr_seq_motivo_result_p
		);


	update	result_laboratorio
	set	ie_status = ie_opcao_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_result_laborat_w;


	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
