CREATE OR REPLACE PROCEDURE inativa_usuario_ci (cd_pessoa_fisica_p text) AS $body$
DECLARE


nm_usuario_w      varchar(250);

c01 CURSOR FOR
	SELECT 	b.nm_usuario
	from 	pessoa_fisica a,
		usuario b
	where 	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and   	a.cd_pessoa_fisica = cd_pessoa_fisica_p;


BEGIN

open c01;
loop
fetch c01 into
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	begin
	update usuario_grupo set ie_situacao = 'I'
	where nm_usuario_grupo = nm_usuario_w;
	commit;
	end;

end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
