CREATE OR REPLACE PROCEDURE inat_medic_fora_ini_prescr ( nr_prescricao_p bigint, nr_seq_item_p bigint, nr_seq_horario_p bigint, dt_horario_p timestamp) AS $body$
DECLARE


dt_inicio_prescr_w		timestamp;

BEGIN
--- Verifica se as variavéis não são nula
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '')	and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '')		and (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '')	then
	--- Busca a data de ínicio da prescricao
	select	max(dt_inicio_prescr)
	into STRICT 	dt_inicio_prescr_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;
	--- Verifica se a variavél de data não é nula
	if (dt_inicio_prescr_w > dt_horario_p) then
		--- Atualiza o status dos itens com horario abaixo do horário de inicio da prescricao
		update	prescr_mat_hor
		set 	ie_situacao 	= 'I'
		where	nr_seq_material = nr_seq_item_p
		and 	nr_prescricao	= nr_prescricao_p
		and 	nr_sequencia    = nr_seq_horario_p;
		commit;
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
