CREATE OR REPLACE PROCEDURE incluir_antib_adicional ( nr_seq_resultado_p bigint, nr_seq_result_item_p bigint, cd_microorganismo_p bigint, cd_medicamento_p bigint, nm_usuario_p text, nr_cultura_microorg_p bigint) AS $body$
DECLARE


nr_seq_result_antib_w           bigint;


BEGIN

select 	coalesce(max(nr_sequencia),0)
into STRICT   	nr_seq_result_antib_w
from   	exame_lab_result_antib
where  	nr_seq_resultado = nr_seq_resultado_p
and    	nr_seq_result_item = nr_seq_result_item_p
and    	cd_microorganismo = cd_microorganismo_p
and		coalesce(nr_cultura_microorg,0) = coalesce(nr_cultura_microorg_p,0);

if (nr_seq_result_antib_w <> 0) then
        begin

        insert into exame_lab_result_antib_adi(
                nr_sequencia,
                dt_atualizacao,
                nm_usuario,
                dt_atualizacao_nrec,
                nm_usuario_nrec,
                nr_seq_exame_lab_result_antib,
                cd_medicamento
				)
        values ( nextval('exame_lab_result_antib_adi_seq'),
                clock_timestamp(),
                nm_usuario_p,
                clock_timestamp(),
                nm_usuario_p,
                nr_seq_result_antib_w,
                cd_medicamento_p);
        end;
end if;

commit;
end;
$body$
