SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE incluir_benef_via_adicional ( nr_seq_segurado_p bigint, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_carteira_w	bigint;
qt_beneficiarios	bigint;
nm_beneficiario_w	varchar(255);


BEGIN

select	max(b.nr_sequencia)
into STRICT	nr_seq_carteira_w
from	pls_segurado_carteira b,
	pls_segurado a
where	a.nr_sequencia = b.nr_seq_segurado
and	a.nr_sequencia = nr_seq_segurado_p;

select	b.nm_pessoa_fisica
into STRICT	nm_beneficiario_w
from	pessoa_fisica	b,
	pls_segurado	a
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
and	a.nr_sequencia = nr_seq_segurado_p;

if (nr_seq_carteira_w IS NOT NULL AND nr_seq_carteira_w::text <> '') then

	select	count(*)
	into STRICT	qt_beneficiarios
	from	pls_via_adic_cart
	where	nr_seq_lote = nr_seq_lote_p
	and	nr_seq_segurado = nr_seq_segurado_p;

	if (qt_beneficiarios = 0) then

		insert	into	pls_via_adic_cart(	nr_sequencia, nr_seq_lote, nr_seq_carteira,
				nr_seq_segurado, ie_origem_solic, dt_atualizacao,
				nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec)
			values (	nextval('pls_via_adic_cart_seq'), nr_seq_lote_p, nr_seq_carteira_w,
				nr_seq_segurado_p, 'M', clock_timestamp(),
				nm_usuario_p, clock_timestamp(), nm_usuario_p);
	else
		CALL wheb_mensagem_pck.exibir_mensagem_abort(190857, 'NM_BENEFICIARIO=' || nm_beneficiario_w || ';NR_SEQ_LOTE=' || nr_seq_lote_p);
	end if;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(190851, 'NM_BENEFICIARIO=' || nm_beneficiario_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
