CREATE OR REPLACE PROCEDURE incluir_dados_negociacao_audit (nr_seq_lote_audit_p text, nr_seq_tipo_hist_p bigint, ds_historico_p text, nm_usuario_p text, ie_opcao_p text, dt_prevista_contato_p timestamp, nm_usuario_resp_p text) AS $body$
DECLARE


/*ie_opcao_p
1 - Histórico
2 - Usuário responsável
-*/
nr_seq_lote_audit_w	bigint;
nr_seq_resp_w		bigint;

c01 CURSOR FOR
SELECT	distinct nr_sequencia
from	lote_auditoria
where	obter_se_contido(nr_sequencia,nr_seq_lote_audit_p) = 'S';


BEGIN

if (nr_seq_lote_audit_p IS NOT NULL AND nr_seq_lote_audit_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_seq_lote_audit_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		if (ie_opcao_p = '1') and (ds_historico_p IS NOT NULL AND ds_historico_p::text <> '') then

			insert into negociacao_lote_audit_hist(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_historico,
				nr_seq_tipo,
				ds_historico,
				nr_seq_lote_audit)
			values (nextval('negociacao_lote_audit_hist_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_tipo_hist_p,
				ds_historico_p,
				nr_seq_lote_audit_w);

			CALL ATUALIZAR_ULTIMO_CONTATO_AUDIT(nm_usuario_p,nr_seq_lote_audit_w);

		elsif (ie_opcao_p = '2') and (nm_usuario_resp_p IS NOT NULL AND nm_usuario_resp_p::text <> '') then

			select	max(nr_sequencia)
			into STRICT	nr_seq_resp_w
			from	negociacao_lote_audit_resp
			where	nm_usuario_resp		= nm_usuario_resp_p
			and	nr_seq_lote_audit	= nr_seq_lote_audit_w;

			if (coalesce(nr_seq_resp_w::text, '') = '') then

				insert into negociacao_lote_audit_resp(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nm_usuario_resp,
					dt_previsao_contato,
					dt_ultimo_contato,
					ds_observacao,
					nr_seq_lote_audit)
				values (nextval('negociacao_lote_audit_resp_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nm_usuario_resp_p,
					dt_prevista_contato_p,
					null,
					null,
					nr_seq_lote_audit_w);
			else
				update	negociacao_lote_audit_resp
				set	dt_previsao_contato	= coalesce(dt_prevista_contato_p,dt_previsao_contato)
				where	nr_sequencia		= nr_seq_resp_w;

			end if;
		end if;

		commit;

	end loop;
	close C01;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE incluir_dados_negociacao_audit (nr_seq_lote_audit_p text, nr_seq_tipo_hist_p bigint, ds_historico_p text, nm_usuario_p text, ie_opcao_p text, dt_prevista_contato_p timestamp, nm_usuario_resp_p text) FROM PUBLIC;

