CREATE OR REPLACE PROCEDURE incluir_grupo_part_ata ( NR_SEQ_ATA_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);
cd_setor_atendimento_w	usuario.cd_setor_atendimento%type;

C01 CURSOR FOR 
	SELECT	a.cd_pessoa_fisica 
	from	usuario		a, 
		usuario_grupo	b 
	where	a.nm_usuario	= b.nm_usuario_grupo 
	and	b.nr_seq_grupo	= nr_seq_grupo_p;				
 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ X] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 

BEGIN 
open C01;
loop 
fetch C01 into	 
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	max(cd_setor_atendimento) 
	into STRICT	cd_setor_atendimento_w 
	from	usuario 
	where	ie_situacao = 'A' 
	and	cd_pessoa_fisica = cd_pessoa_fisica_w;
 
	INSERT INTO PROJ_ATA_PARTICIPANTE(nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		NR_SEQ_ATA, 
		cd_pessoa_participante, 
		ie_faltou, 
		ds_email, 
		cd_setor_atendimento, 
		nm_pessoa_participante) 
	VALUES (nextval('proj_ata_participante_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		NR_SEQ_ATA_p, 
		cd_pessoa_fisica_w, 
		'N', 
		substr(obter_compl_pf(cd_pessoa_fisica_w,1,'M'),1,255), 
		cd_setor_atendimento_w, 
		substr(obter_nome_pf(cd_pessoa_fisica_w),1,60));
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
