SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE incluir_idioma_nac ( cd_nacionalidade_p text, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_idioma_w		bigint;
nr_seq_idioma_pf_w	bigint;



BEGIN


if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(nr_seq_idioma)
	into STRICT	nr_seq_idioma_w
	from	nacionalidade
	where	cd_nacionalidade	=	cd_nacionalidade_p;

	if (nr_seq_idioma_w IS NOT NULL AND nr_seq_idioma_w::text <> '') then

		select	max(nr_seq_idioma)
		into STRICT	nr_seq_idioma_pf_w
		from	pessoa_fisica_idioma
		where	cd_pessoa_fisica	=	cd_pessoa_fisica_p;

		if (coalesce(nr_seq_idioma_pf_w::text, '') = '') then

			insert into pessoa_fisica_idioma(cd_pessoa_fisica,
				dt_atualizacao,
				dt_atualizacao_nrec,
				ie_fluencia,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_idioma,
				nr_sequencia)
			values (cd_pessoa_fisica_p,
				clock_timestamp(),
				clock_timestamp(),
				'F',
				nm_usuario_p,
				nm_usuario_p,
				nr_seq_idioma_w,
				nextval('pessoa_fisica_idioma_seq'));

		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
