CREATE OR REPLACE PROCEDURE incluir_liberacao_exc_cobr ( ie_tipo_cobranca_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w 		bigint;
ds_historico_w		varchar(255);
nr_titulo_w		titulo_receber_orgao_cobr.nr_titulo%type;
nr_seq_cheque_w		cheque_cr_orgao_cobr.nr_seq_cheque%type;
qt_w				bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_cheque
	from	cheque_cr_orgao_cobr
	where	ie_selecionado = 'S'
	and 	coalesce(dt_liberacao_exclusao::text, '') = ''
	and 	coalesce(dt_exclusao::text, '') = ''
	and	coalesce(nr_seq_lote_exc::text, '') = '';

C02 CURSOR FOR
	SELECT	nr_sequencia,
		nr_titulo
	from	titulo_receber_orgao_cobr
	where	ie_selecionado = 'S'
	and 	coalesce(dt_liberacao_exclusao::text, '') = ''
	and 	coalesce(dt_exclusao::text, '') = ''
	and	coalesce(nr_seq_lote_exc::text, '') = '';


C03 CURSOR FOR
	SELECT	nr_sequencia
	from	outros_orgao_cobr
	where	ie_selecionado = 'S'
	and 	coalesce(dt_liberacao_exclusao::text, '') = ''
	and 	coalesce(dt_exclusao::text, '') = ''
	and	coalesce(nr_seq_lote_exc::text, '') = '';


BEGIN

if (ie_tipo_cobranca_p = 'C') then

	select	count(*)
	into STRICT	qt_w
	from	cheque_cr_orgao_cobr
	where	ie_selecionado = 'S';

	if (qt_w = 0)then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(682121);
	end if;

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w,
		nr_seq_cheque_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	cheque_cr_orgao_cobr
		set	dt_liberacao_exclusao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_w;

		update	cheque_cr_orgao_cobr
		set	ie_selecionado = 'N'
		where	nr_sequencia = nr_sequencia_w;

		ds_historico_w := wheb_mensagem_pck.get_texto(338832, 'NM_USUARIO=' || nm_usuario_p || ';NR_SEQUENCIA=' || nr_seq_cheque_w || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
		CALL gerar_historico_cobr_orgao(null,nr_seq_cheque_w,null,ds_historico_w,nm_usuario_p,'P');

		end;
	end loop;
	close C01;

elsif (ie_tipo_cobranca_p = 'T') then

	select	count(*)
	into STRICT	qt_w
	from	titulo_receber_orgao_cobr
	where	ie_selecionado = 'S';

	if (qt_w = 0)then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(682121);
	end if;

	open C02;
	loop
	fetch C02 into
		nr_sequencia_w,
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		update	titulo_receber_orgao_cobr
		set	dt_liberacao_exclusao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_w;

		update	titulo_receber_orgao_cobr
		set	ie_selecionado = 'N'
		where	nr_sequencia = nr_sequencia_w;

		ds_historico_w := wheb_mensagem_pck.get_texto(338832, 'NM_USUARIO=' || nm_usuario_p || ';NR_SEQUENCIA=' || nr_titulo_w || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
		CALL gerar_historico_cobr_orgao(nr_titulo_w,null,null,ds_historico_w,nm_usuario_p,'P');

		end;
	end loop;
	close C02;

elsif (ie_tipo_cobranca_p = 'O') then

	select	count(*)
	into STRICT	qt_w
	from	outros_orgao_cobr
	where	ie_selecionado = 'S';

	if (qt_w = 0)then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(682121);
	end if;

	open C03;
	loop
	fetch C03 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin

		update	outros_orgao_cobr
		set	dt_liberacao_exclusao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_w;

		update	outros_orgao_cobr
		set	ie_selecionado = 'N'
		where	nr_sequencia = nr_sequencia_w;

		ds_historico_w := wheb_mensagem_pck.get_texto(338832, 'NM_USUARIO=' || nm_usuario_p || ';NR_SEQUENCIA=' || nr_sequencia_w || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
		CALL gerar_historico_cobr_orgao(null,null,nr_sequencia_w,ds_historico_w,nm_usuario_p,'P');

		end;
	end loop;
	close C03;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
