SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE incluir_morfologia_laudo ( nr_seq_laudo_p bigint, nr_seq_peca_p bigint, ds_lista_morfologia_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_w		varchar(255);
ie_pos_virgula_w	bigint;
tam_lista_w		bigint;

cd_morfologia_w		varchar(10);


BEGIN

ds_lista_w := ds_lista_morfologia_p;

while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '')  loop
	begin

	tam_lista_w	 := length(ds_lista_w);
	ie_pos_virgula_w := position(',' in ds_lista_w);

	if (ie_pos_virgula_w <> 0) then
		cd_morfologia_w	:= substr(ds_lista_w,1,(ie_pos_virgula_w - 1));
		ds_lista_w	:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;


	insert	into	laudo_cido_morfologia(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_laudo,
		cd_morfologia,
		nr_seq_prescr_peca
		)
	values (
		nextval('laudo_cido_morfologia_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_laudo_p,
		cd_morfologia_w,
		nr_seq_peca_p
		);

	end;
	end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
