CREATE OR REPLACE PROCEDURE incluir_numero_ctrl_prescr ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_controle_p text, nm_usuario_p text ) AS $body$
BEGIN

if (coalesce(nr_seq_prescr_p, 0) = 0) then
	UPDATE 	prescr_medica
	SET 	nr_controle = nr_controle_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	WHERE 	nr_prescricao = nr_prescricao_p;

else
	UPDATE 	prescr_procedimento
	SET 	nr_controle_ext = nr_controle_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	WHERE 	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_prescr_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
