CREATE OR REPLACE PROCEDURE incluir_opmes_agenda ( nr_seq_agenda_p bigint, nm_usuario_p text, ds_lista_opme_p text) AS $body$
DECLARE

ds_lista_opme_w		varchar(10000);
qt_tam_lista_w		bigint;
ie_pos_virgula_w 	integer;
cd_material_w		bigint;

BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ds_lista_opme_p IS NOT NULL AND ds_lista_opme_p::text <> '') then
	begin
	ds_lista_opme_w := ds_lista_opme_p;

	while(ds_lista_opme_w IS NOT NULL AND ds_lista_opme_w::text <> '') loop
		begin
		qt_tam_lista_w		:= length(ds_lista_opme_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_opme_w);

		if (ie_pos_virgula_w <> 0) then
			begin
			cd_material_w	:= substr(ds_lista_opme_w,1,(ie_pos_virgula_w - 1));
			ds_lista_opme_w	:= substr(ds_lista_opme_w,(ie_pos_virgula_w + 1),qt_tam_lista_w);
			end;
		else
			begin
			cd_material_w	:= (ds_lista_opme_w)::numeric;
			ds_lista_opme_w	:= null;
			end;
		end if;

		if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') and (cd_material_w > 0) then
			begin
			CALL incluir_opme_agenda(
				nr_seq_agenda_p,
				cd_material_w,
				nm_usuario_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
end;
$body$
