SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE incluir_os_sla ( nr_sequencia_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


ie_possui_classif_w	varchar(1);
ie_os_sla_w			varchar(1);

BEGIN

select  CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_os_sla_w
from    man_ordem_serv_sla
where   nr_seq_ordem = nr_sequencia_p;

select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_possui_classif_w
from	man_ordem_servico
where	nr_sequencia = nr_sequencia_p
and		(ie_classificacao IS NOT NULL AND ie_classificacao::text <> '');

if (ie_os_sla_w = 'S') then
	begin
		ds_retorno_p := wheb_mensagem_pck.get_texto(224949); -- Esta OS já está cadastrada como SLA.
		goto final;
	end;
end if;

if (ie_possui_classif_w = 'N') then
	begin
		ds_retorno_p := wheb_mensagem_pck.get_texto(319235); --  É necessário selecionar uma Classificação para a OS.
		goto final;
	end;
end if;


CALL man_gerar_os_sla(nr_sequencia_p, nm_usuario_p);
ds_retorno_p := wheb_mensagem_pck.get_texto(224950);

<<final>>

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
