SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE incluir_tx_espec_tec_mat ( nm_usuario_p text, cd_material_p bigint) AS $body$
DECLARE


qt_registro_w		bigint := 0;
nr_sequencia_w		material_espec_tecnica.nr_sequencia%type;
nr_seq_rtf_srtring_w	varchar(50);
ds_espec_w		varchar(4000);


BEGIN


select count(*)
into STRICT qt_registro_w
from material_espec_tecnica
where cd_material = cd_material_p;

if (qt_registro_w > 0) then
	begin

	select  max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from    material_espec_tecnica
	where   cd_material = cd_material_p;


	nr_seq_rtf_srtring_w := converte_rtf_string('select ds_espec_tecnica
			     from  material_espec_tecnica
			     where nr_sequencia = :nr_sequencia_p', nr_sequencia_w, nm_usuario_p, nr_seq_rtf_srtring_w);

	begin

	select	ds_texto
	into STRICT	ds_espec_w
	from	tasy_conversao_rtf
	where	nr_sequencia = nr_seq_rtf_srtring_w;

	exception
	when others then
		ds_espec_w := null;
	end;


	end;

	if (coalesce(ds_espec_w::text, '') = '')then
		ds_espec_w := convert_long_html_to_str('ds_espec_tecnica', 'material_espec_tecnica', 'nr_sequencia = ' ||  nr_sequencia_w);
	end if;

	if (ds_espec_w IS NOT NULL AND ds_espec_w::text <> '') then

	update 	material_espec_tecnica

	set 	ds_espec_tecnica_tx = ds_espec_w
	where 	nr_sequencia = nr_sequencia_w;

	commit;
	end if;

end if;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
