CREATE OR REPLACE PROCEDURE inf_aprov_desaprov_busca_cli ( ie_aprov_desaprov_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN
	if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_aprov_desaprov_p IS NOT NULL AND ie_aprov_desaprov_p::text <> '') then
		begin
		if (ie_aprov_desaprov_p = 'A') then
			begin
			update	busca_inf_base_cliente
			set	nm_usuario_aprov = nm_usuario_p,
				dt_aprov = clock_timestamp()
			where	nr_sequencia = nr_sequencia_p;
			end;
		elsif (ie_aprov_desaprov_p = 'D') then
			begin
			update	busca_inf_base_cliente
			set	nm_usuario_aprov = '',
				nm_usuario = nm_usuario_p,
				dt_aprov  = NULL
			where	nr_sequencia = nr_sequencia_p;
			end;
		end if;
		end;
	end if;

commit;

end;
$body$
