CREATE OR REPLACE PROCEDURE iniciar_checkup ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_possui_inicio_real_w	varchar(1) := 'N';


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	CASE WHEN coalesce(dt_inicio_real::text, '') = '' THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_inicio_real_w
	from	checkup
	where	nr_sequencia = nr_sequencia_p;

	if ('N' = ie_possui_inicio_real_w) then
		begin
		update	checkup
		set	dt_inicio_real	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_p;
		end;
	end if;
	end;
end if;
commit;
end;
