CREATE OR REPLACE PROCEDURE iniciar_coleta_doacao (nr_seq_doacao_p bigint, nm_usuario_p text, dt_inicio_coleta_p timestamp default clock_timestamp()) AS $body$
DECLARE


ie_param_478_w	varchar(1) := 'N';


BEGIN

ie_param_478_w 	:= Obter_Valor_Param_Usuario(450, 478, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);

if ie_param_478_w = 'S' then
	update 	san_doacao
	set	dt_inicio_coleta 	= coalesce(dt_inicio_coleta_p,clock_timestamp()),
		nm_usuario_ini_coleta 	= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		cd_pessoa_inicio_coleta = obter_pf_usuario(nm_usuario_p, 'C')
	where 	nr_sequencia		= nr_seq_doacao_p;
else
	update 	san_doacao
	set	dt_inicio_coleta 	= coalesce(dt_inicio_coleta_p,clock_timestamp()),
		nm_usuario_ini_coleta 	= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where 	nr_sequencia		= nr_seq_doacao_p;

end if;

commit;

end;
