SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE iniciar_finalizar_conf_presc ( nr_sequencia_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
BEGIN
if (coalesce(nr_sequencia_p,0) > 0) and (ie_Acao_p IS NOT NULL AND ie_Acao_p::text <> '') then

	if (coalesce(ie_Acao_p,'x') = 'I') then

		update	prescr_lib_conferencia
		set	dt_inicio_conferencia	= clock_timestamp(),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			nm_usuario_conferencia  = nm_usuario_p
		where	nr_sequencia 		= nr_sequencia_p;

	elsif (coalesce(ie_Acao_p,'x') = 'F') then

		update	prescr_lib_conferencia
		set	DT_FIM_CONFERENCIA	= clock_timestamp(),
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia 		= nr_sequencia_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
