CREATE OR REPLACE PROCEDURE iniciar_finalizar_consulta_job ( qt_horas_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	oft_consulta
	where	dt_consulta < clock_timestamp() - (qt_horas_p/24)
	and	coalesce(dt_fim_consulta::text, '') = ''
	and	coalesce(dt_cancelamento::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL iniciar_finalizar_consulta(nr_sequencia_w,'F',nm_usuario_p,cd_estabelecimento_p);

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
