SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE iniciar_finalizar_consulta ( nr_seq_consulta_p bigint, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_status_cons_w      bigint := null;
nr_seq_agenda_w		  bigint;
ie_agenda_w		  varchar(2) := 'CI';
nr_atendimento_w  atendimento_paciente.nr_atendimento%type;
nr_seq_tipo_consulta_w  OFT_CONSULTA.NR_SEQ_TIPO_CONSULTA%type;

agendas CURSOR FOR
	SELECT 	nr_sequencia,
				'CI' ie_agenda
	from   	agenda_paciente
	where  	ie_status_agenda not in ('L','C','E')
	and		nr_seq_oftalmo = nr_seq_consulta_p
	and		nr_atendimento = (	SELECT	max(nr_atendimento)
											from		oft_consulta
											where		nr_sequencia = nr_seq_consulta_p)
	
union

	select 	nr_sequencia,
				'CI' ie_agenda
	from   	agenda_paciente
	where  	ie_status_agenda not in ('L','C','E')
	and		nr_atendimento = (	select	max(nr_atendimento)
											from	oft_consulta
											where	nr_sequencia = nr_seq_consulta_p)
	
union

	select 	nr_sequencia,
				'C' ie_agenda
	from   	agenda_consulta
	where  	ie_status_agenda not in ('L','C','E')
	and		nr_seq_oftalmo = nr_seq_consulta_p
	and		nr_atendimento = (	select	max(nr_atendimento)
											from		oft_consulta
											where		nr_sequencia = nr_seq_consulta_p)
	
union

	select 	nr_sequencia,
				'C' ie_agenda
	from   	agenda_consulta
	where  	ie_status_agenda not in ('L','C','E')
	and		nr_atendimento = (	select	max(nr_atendimento)
											from		oft_consulta
											where		nr_sequencia = nr_seq_consulta_p);



BEGIN

nr_seq_status_cons_w := obter_param_usuario(3010, 27, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, nr_seq_status_cons_w);

select	coalesce(max(nr_atendimento),0)
into STRICT	nr_atendimento_w
from	oft_consulta
where	nr_sequencia = nr_seq_consulta_p;

if (ie_opcao_p = 'I') then
	update	oft_consulta
	set		dt_consulta		      =	clock_timestamp(),
            dt_inicio_consulta   =  clock_timestamp()
	where		nr_sequencia	      =	nr_seq_consulta_p;
	
	if (nr_atendimento_w > 0) then
		update	atendimento_paciente
		set	dt_atend_medico		= clock_timestamp()
		where	nr_atendimento		= nr_atendimento_w
		and	ie_tipo_atendimento	= 3;
	end if;

	FOR c_agendas IN agendas LOOP
		begin
		CALL executar_evento_agenda('ICO', c_agendas.ie_agenda, c_agendas.nr_sequencia, cd_estabelecimento_p, nm_usuario_p);
		end;
	end loop;

elsif (ie_opcao_p = 'F') then
	update	oft_consulta
	set		dt_fim_consulta	=	clock_timestamp(),
				nr_seq_status		=	CASE WHEN nr_seq_status_cons_w = NULL THEN nr_seq_status  ELSE nr_seq_status_cons_w END
	where		nr_sequencia		=	nr_seq_consulta_p;
	
	if (nr_atendimento_w > 0) then
		update	atendimento_paciente
		set	dt_fim_consulta		= clock_timestamp()
		where	nr_atendimento		= nr_atendimento_w
		and	ie_tipo_atendimento	= 3;
	end if;

	FOR c_agendas IN agendas LOOP
		begin
		CALL executar_evento_agenda('FCO', c_agendas.ie_agenda, c_agendas.nr_sequencia, cd_estabelecimento_p, nm_usuario_p);
		end;
	end loop;

  select max(nr_seq_tipo_consulta)
  into STRICT nr_seq_tipo_consulta_w
  from oft_consulta
  where nr_sequencia = nr_seq_consulta_p;
  CALL ATUALIZAR_EV_LINHA_CUIDADO('CO', 'OFT_CONSULTA', nr_seq_consulta_p, nr_seq_tipo_consulta_w, Obter_pessoa_atendimento(nr_atendimento_w,'C'), nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
