SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE iniciar_finalizar_ordens ( nr_seq_etapa_prod_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = 'I') then
	update can_ordem_prod set
		dt_inicio_preparo = clock_timestamp(),
		nm_usuario_inic_prep = nm_usuario_p
	where 	nr_seq_etapa_prod = nr_seq_etapa_prod_p
	and	coalesce(dt_inicio_preparo::text, '') = '';
elsif (ie_opcao_p = 'F') then
       	update can_ordem_prod set
		dt_fim_preparo = clock_timestamp(),
		nm_usuario_fim_prep = nm_usuario_p
	where 	nr_seq_etapa_prod = nr_seq_etapa_prod_p
	and	(dt_inicio_preparo IS NOT NULL AND dt_inicio_preparo::text <> '')
	and	coalesce(dt_fim_preparo::text, '') = '';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
