		exception
		when others then
			ds_erro_w	:= substr(sqlerrm,1,255);
		end;
	   
       
    ELSIF ie_opcao_p = 'F' THEN
    
      UPDATE triagem_pronto_atend
         SET dt_fim_retriagem	=	clock_timestamp()		
       WHERE nr_sequencia = nr_seq_triagem_p;
	
	   
		begin		
			CALL gerar_log_triagem_pa( null,'F',nm_usuario_p, NULL, 'S', NULL, nr_seq_triagem_p);		
