SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inicia_analise_novos_itens_gpt (nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_lib_farm_p text, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, qt_horas_quimio_p bigint default 0) AS $body$
DECLARE


ie_tipo_usuario_w		varchar(1);
ds_retorno				varchar(1) := 'N';
dt_inicio_analise_w		gpt_hist_analise_plano.dt_inicio_analise%type;
dt_fim_analise_w		gpt_hist_analise_plano.dt_fim_analise%type;
nr_prescricao_w			prescr_medica.nr_prescricao%type;

c01 CURSOR FOR
SELECT	x.nr_prescricao
from	prescr_medica x
where	x.nr_atendimento = nr_atendimento_p
and		x.cd_pessoa_fisica = cd_pessoa_fisica_p
and 	not exists (select	1
					from 	prescr_medica_compl b 
					where	x.nr_prescricao = b.nr_prescricao 
					and		(b.dt_inicio_analise_enf IS NOT NULL AND b.dt_inicio_analise_enf::text <> ''))
and 	coalesce(x.dt_liberacao::text, '') = ''
and		(x.dt_liberacao_medico IS NOT NULL AND x.dt_liberacao_medico::text <> '')
and 	x.cd_funcao_origem 	not in (900,924,950)
and 	x.dt_prescricao >= dt_inicio_analise_w

union

select	x.nr_prescricao
from	prescr_medica x
where	coalesce(x.nr_atendimento::text, '') = ''
and		x.cd_pessoa_fisica = cd_pessoa_fisica_p
and 	not exists (select	1 
					from 	prescr_medica_compl b 
					where	x.nr_prescricao = b.nr_prescricao 
					and		(b.dt_inicio_analise_enf IS NOT NULL AND b.dt_inicio_analise_enf::text <> ''))
and 	coalesce(x.dt_liberacao::text, '') = ''
and		(x.dt_liberacao_medico IS NOT NULL AND x.dt_liberacao_medico::text <> '')
and 	x.cd_funcao_origem 	not in (900,924,950)
and 	x.dt_prescricao >= dt_inicio_analise_w;


BEGIN
CALL wheb_assist_pck.set_informacoes_usuario(cd_estabelecimento_p, obter_perfil_ativo, nm_usuario_p);
ie_tipo_usuario_w  	:= wheb_assist_pck.obterparametrofuncao(252,1);

if (ie_tipo_usuario_w = 'E') then
	dt_inicio_analise_w := obter_dt_analise_gpt(nr_atendimento_p,null,null,'E','DTI', ie_lib_farm_p, cd_pessoa_fisica_p);
	
	open c01;
	loop
	fetch c01 into
		nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		CALL gerar_prescr_medica_compl(	nr_prescricao_w, nm_usuario_p, nm_usuario_p, clock_timestamp());
		end;
	end loop;
	close c01;

elsif (ie_tipo_usuario_w = 'F') then

	dt_inicio_analise_w := obter_dt_analise_gpt(nr_atendimento_p, null, null, 'F', 'DTI', ie_lib_farm_p, cd_pessoa_fisica_p);
	dt_fim_analise_w := obter_dt_analise_gpt(nr_atendimento_p, null, null, 'F', 'DTF', ie_lib_farm_p, cd_pessoa_fisica_p);

	update	prescr_medica
	set		dt_inicio_analise_farm = clock_timestamp(),
			nm_usuario_analise_farm = nm_usuario_p
	where	nr_prescricao in (SELECT x.nr_prescricao
			from	prescr_medica x
			where	((x.nr_atendimento = nr_atendimento_p) or (x.cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(x.nr_atendimento::text, '') = '')) --AQUI
			and (ie_lib_farm_p = 'N' or coalesce(x.ie_lib_farm,'N') = 'S')
			and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
			and		coalesce(x.dt_liberacao_farmacia::text, '') = ''
			and		coalesce(x.dt_inicio_analise_farm::text, '') = ''
			and		coalesce(x.nm_usuario_analise_farm::text, '') = ''
			and		((coalesce(x.nr_seq_atend::text, '') = '' and x.dt_prescricao >= dt_inicio_analise_w) or (coalesce(x.nr_seq_atend::text, '') = '' and ie_tipo_usuario_w = 'F' and x.dt_liberacao >= dt_inicio_analise_w and coalesce(dt_fim_analise_w::text, '') = '') or
					((x.nr_seq_atend IS NOT NULL AND x.nr_seq_atend::text <> '') and x.dt_prescricao between inicio_dia(clock_timestamp() - interval '3 days') and clock_timestamp() + (qt_horas_quimio_p / 24))));

	commit;

end if;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inicia_analise_novos_itens_gpt (nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_lib_farm_p text, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, qt_horas_quimio_p bigint default 0) FROM PUBLIC;

