CREATE OR REPLACE PROCEDURE insere_agenda_cons_cont_secao ( dt_agenda_p timestamp, nr_seq_agenda_p bigint, nm_usuario_p text, nr_controle_p INOUT bigint) AS $body$
DECLARE


nr_controle_secao_w	bigint;
age_cons_nr_cont_secao_w	bigint;

nr_secao_w		bigint;
cd_procedimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
qt_total_secao_w		smallint;
nm_paciente_w		varchar(80);


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	select	coalesce(max(nr_secao),0),
		max(cd_procedimento),
		max(cd_pessoa_fisica),
		max(qt_total_secao),
		max(nm_paciente)
	into STRICT	nr_secao_w,
		cd_procedimento_w,
		cd_pessoa_fisica_w,
		qt_total_secao_w,
		nm_paciente_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	if (nr_secao_w > 1) then
		select	coalesce(max(nr_controle_secao),0)
		into STRICT	nr_controle_p
		from	agenda_consulta
		where	((cd_pessoa_fisica = cd_pessoa_fisica_w) or (nm_paciente = nm_paciente_w))
		and	((cd_procedimento  = cd_procedimento_w) or (coalesce(cd_procedimento_w::text, '') = ''));
	elsif (nr_secao_w = 0) then
		select	coalesce(max(nr_controle_secao),0)+1
		into STRICT	nr_controle_secao_w
		from	agenda_consulta
		where	dt_agenda between clock_timestamp() - interval '5 days' and clock_timestamp() + interval '365 days';

		select	max(nr_controle_secao)+1
		into STRICT	age_cons_nr_cont_secao_w
		from 	agenda_consulta_cont_secao
		where	dt_atualizacao between clock_timestamp() - interval '30 days' and clock_timestamp() + interval '30 days';

		if (age_cons_nr_cont_secao_w > nr_controle_secao_w) then
			nr_controle_p := age_cons_nr_cont_secao_w;
		else
			nr_controle_p := nr_controle_secao_w;
		end if;

		if (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then

		insert into agenda_consulta_cont_secao(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_controle_secao,
				dt_agenda)
			values (
				nextval('agenda_consulta_cont_secao_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_controle_p,
				dt_agenda_p);

		end if;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
