CREATE OR REPLACE PROCEDURE insere_aval_tipo_hist ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, cd_pessoa_fisica_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_avaliacao_w	bigint;
nr_seq_tipo_aval_w	bigint;


BEGIN

select	nextval('pessoa_fisica_avaliacao_seq')
into STRICT	nr_seq_avaliacao_w
;

select	max(a.nr_seq_tipo_avaliacao)
into STRICT	nr_seq_tipo_aval_w
from   	tipo_hist_pessoa_fisica a,
     	pessoa_fisica_historico b
where   b.nr_seq_tipo_hist = a.nr_sequencia
and	(a.nr_seq_tipo_avaliacao IS NOT NULL AND a.nr_seq_tipo_avaliacao::text <> '')
and	a.ie_situacao = 'A'
and	b.nr_sequencia = nr_sequencia_p;

if (coalesce(nr_seq_tipo_aval_w,0) > 0) then
	begin
	insert into pessoa_fisica_avaliacao(
			nr_sequencia,
			cd_estabelecimento,
			cd_pessoa_fisica,
			dt_atualizacao,
			dt_atualizacao_nrec,
			dt_avaliacao,
			dt_liberacao,
			dt_prox_avaliacao,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_tipo_avaliacao)
		values (	nr_seq_avaliacao_w,
			coalesce(cd_estabelecimento_p,wheb_usuario_pck.get_cd_estabelecimento),
			cd_pessoa_fisica_p,
			clock_timestamp(),
			clock_timestamp(),
			clock_timestamp(),
			null,
			null,
			nm_usuario_p,
			nm_usuario_p,
			nr_seq_tipo_aval_w);
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
