CREATE OR REPLACE PROCEDURE insere_campo_banda_rel ( nm_Coluna_p text, qt_tamanho_p bigint, nr_seq_banda_p bigint, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_apresentacao_w		bigint;


BEGIN

select	coalesce(max(nr_seq_apresentacao),0)+1
into STRICT	nr_seq_apresentacao_w
from	Banda_Relat_Campo
where	nr_seq_banda = nr_seq_banda_p;

insert into banda_relat_campo(nr_sequencia, nm_usuario, dt_atualizacao,
		 ie_tipo_campo, nr_seq_banda, nr_seq_apresentacao,
		 qt_altura, qt_tamanho, qt_esquerda,
		 qt_topo, ie_quebra_pagina, ie_transparente,
		 ie_totalizar, ie_ajustar_tamanho, ie_alinhar_banda,
		 ie_estender, ie_zera_apos_imprimir, ie_campo_quebra,
		 ie_borda_sup, ie_borda_inf, ie_borda_esq,
		 ie_borda_dir, ie_alinhamento, ds_cor_fundo,
		 ds_cor_label, ds_cor_fonte, ds_tipo_fonte,
		 qt_tam_fonte, pr_altura_barcode, qt_fonte_barcode,
		 ie_humano_barcode, ie_altera_valor, ie_tipo_barcode,
		 ds_campo, nm_atributo)
values (nextval('banda_relat_campo_seq'), nm_usuario_p, clock_timestamp(),
		 0, nr_seq_banda_p, nr_seq_apresentacao_w,
		 17, qt_tamanho_p, 5,
		 1, 'N', 'S',
		 'N', 'N', 'N',
		 'N', 'N', 'N',
		 'N', 'N', 'N',
		 'N', 'E', 'clWhite',
		 'clWhite', 'clBlack', 'Arial',
		 8, 50, 100,
		 'S', 'N', 'CODE128_C',
		 nm_Coluna_p, nm_Coluna_p);

