CREATE OR REPLACE PROCEDURE insere_cnes_profissional ( ie_sus_p text, nm_usuario_p text, nr_seq_identificacao_p bigint, cd_cbo_p text, nr_registro_p text, nr_seq_vinculo_p bigint, qt_hora_ambulatorial_p text, qt_hora_hospitalar_p text, qt_hora_outros_p text, sg_uf_emissor_p text, ie_insere_prof_p text, cpf_p text, nr_cartao_nac_sus_ant_p text) AS $body$
DECLARE

					
nr_seq_profissional_w	cnes_profissional.nr_sequencia%type;
cd_pessoa_fisica_w	cnes_profissional.cd_pessoa_fisica%type;
nr_sequencia_vinculo_w	cnes_vinculo.nr_sequencia%type;
qt_existe_w		bigint;
nr_seq_vinculo_w	cnes_tipo_vinculo.nr_seq_vinculo%type;
nr_seq_tipo_vinculo_w   cnes_sub_tipo_vinculo.nr_seq_tipo_vinculo%type;
nr_sequencia_tipo_vinculo_w      cnes_tipo_vinculo.nr_sequencia%type;
nr_seq_sub_tipo_vinculo_w   cnes_sub_tipo_vinculo.nr_sequencia%type;


BEGIN

select	coalesce(max(cd_pessoa_fisica),0)
into STRICT	cd_pessoa_fisica_w
from	pessoa_fisica
where	nr_cpf = cpf_p;

select 	count(*)
into STRICT	qt_existe_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_w;

if (qt_existe_w > 0) then
	if (coalesce(SG_UF_EMISSOR_P,'X') <> 'X') then
		if (coalesce(ie_insere_prof_p,'X') = 'I') then
			insert into cnes_profissional(	cd_pessoa_fisica,
							dt_atualizacao,
                            dt_atualizacao_nrec,
							ie_sus,
							nm_usuario,
                            nm_usuario_nrec,
							nr_sequencia,
							nr_seq_identificacao,
                            nr_cartao_nac_sus_ant)
						values (	cd_pessoa_fisica_w,
							clock_timestamp(),
                            clock_timestamp(),
							ie_sus_p,
							nm_usuario_p,
                            nm_usuario_p,
							nextval('cnes_profissional_seq'),
							nr_seq_identificacao_p,
                            nr_cartao_nac_sus_ant_p);

			commit;

		end if;

        begin
		select	max(nr_sequencia)
		into STRICT	nr_seq_profissional_w
		from	cnes_profissional
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;
        exception
                when others then
                cd_pessoa_fisica_w := null;
        end;

        begin
        select  nr_seq_tipo_vinculo,
                nr_sequencia
        into STRICT	nr_seq_tipo_vinculo_w,
                nr_seq_sub_tipo_vinculo_w
        from    cnes_sub_tipo_vinculo
        where   cd_subtipo_vinculo = nr_seq_vinculo_p
        and     ie_situacao = 'A';
        exception
                when others then
                nr_seq_tipo_vinculo_w := null;
                nr_seq_sub_tipo_vinculo_w := null;
        end;

        begin
        select  nr_seq_vinculo,
                nr_sequencia
        into STRICT	nr_seq_vinculo_w,
                nr_sequencia_tipo_vinculo_w
        from    cnes_tipo_vinculo
        where   nr_sequencia = nr_seq_tipo_vinculo_w
        and     ie_situacao = 'A';
        exception
                when others then
                nr_seq_vinculo_w := null;
                nr_sequencia_tipo_vinculo_w := null;
        end;

        begin
        select  nr_sequencia
        into STRICT    nr_sequencia_vinculo_w
        from	cnes_vinculo
        where   nr_sequencia = nr_seq_vinculo_w
        and     ie_situacao = 'A';
        exception
                when others then
                nr_sequencia_vinculo_w := null;
        end;

		if (nr_seq_tipo_vinculo_w IS NOT NULL AND nr_seq_tipo_vinculo_w::text <> '' AND nr_seq_sub_tipo_vinculo_w IS NOT NULL AND nr_seq_sub_tipo_vinculo_w::text <> '') then
			begin

			insert into cnes_profissional_vinculo(	cd_cbo,
								dt_atualizacao,
								nm_usuario,
								nr_registro,
								nr_seq_profissional,
								nr_sequencia,
								nr_seq_vinculo,
								qt_hora_ambulatorial,
								qt_hora_hospitalar,
								qt_hora_outros,
								ie_atendimento_sus,
								sg_uf_emissor,
								nr_seq_tipo_vinculo,
                                nr_seq_subtp_vinculo)
						values (		cd_cbo_p,
								clock_timestamp(),
								nm_usuario_p,
								nr_registro_p,
								nr_seq_profissional_w,
								nextval('cnes_profissional_vinculo_seq'),
								nr_sequencia_vinculo_w,
								qt_hora_ambulatorial_p,
								qt_hora_hospitalar_p,
								qt_hora_outros_p,
								ie_sus_p,
								sg_uf_emissor_p,
								nr_sequencia_tipo_vinculo_w,
                                nr_seq_sub_tipo_vinculo_w);
			commit;

			end;
		end if;

	end if;
end if;

end;
