CREATE OR REPLACE PROCEDURE insere_espec_medica_atend ( NR_ATENDIMENTO_P bigint, CD_ESPEC_MEDICO_RESP_P bigint) AS $body$
DECLARE


QTD_REGISTRO_W   varchar(10);


BEGIN

select  count(NR_ATENDIMENTO)
into STRICT  QTD_REGISTRO_W
from  ATEND_PACIENTE_ADIC
where  nr_atendimento = NR_ATENDIMENTO_P;

if (coalesce(QTD_REGISTRO_W,0) = 0)then
 insert into ATEND_PACIENTE_ADIC(
         DT_ATUALIZACAO,
         NM_USUARIO,
         DT_ATUALIZACAO_NREC,
         NM_USUARIO_NREC,
         NR_ATENDIMENTO,
         CD_ESPEC_MEDICO_RESP
         )
 values (
         clock_timestamp(),
         OBTER_USUARIO_ATIVO(),
         clock_timestamp(),
         OBTER_USUARIO_ATIVO(),
         NR_ATENDIMENTO_P,
         CD_ESPEC_MEDICO_RESP_P
         );

else

 update  ATEND_PACIENTE_ADIC
 set  DT_ATUALIZACAO = clock_timestamp(),
   NM_USUARIO = OBTER_USUARIO_ATIVO(),
   CD_ESPEC_MEDICO_RESP = CD_ESPEC_MEDICO_RESP_P
 where NR_ATENDIMENTO = NR_ATENDIMENTO_P;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
