CREATE OR REPLACE PROCEDURE insere_itens_fatur_paciente ( nr_seq_item_p bigint, nr_atendimento_p bigint, cd_guia_p text, cd_medico_solic_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_plano_w		bigint;
vl_item_w			double precision;
ie_tipo_guia_w		varchar(2);
ie_tipo_atend_tiss_w	varchar(10);
nr_sequencia_w		bigint;


BEGIN

select	max(nr_seq_plano)
into STRICT	nr_seq_plano_w
from	med_atendimento
where 	nr_atendimento = nr_atendimento_p;

select 	coalesce(max(vl_padrao),0),
	max(ie_tipo_atend_tiss),
	max(ie_tipo_guia)
into STRICT	vl_item_w,
	ie_tipo_atend_tiss_w,
	ie_tipo_guia_w
from 	med_item_convenio
where 	nr_seq_plano   	= nr_seq_plano_w
and   	nr_seq_item    	= nr_seq_item_p;

select	nextval('med_faturamento_seq')
into STRICT	nr_sequencia_w
;

insert into med_faturamento(nr_sequencia,
		nr_atendimento,
		nr_seq_item,
		dt_atualizacao,
		nm_usuario,
		qt_item,
		vl_item,
		nr_seq_protocolo,
		ie_tipo_guia,
		cd_guia,
		ie_tipo_atend_tiss,
		cd_medico_solic)
values (nr_sequencia_w,
		nr_atendimento_p,
		nr_seq_item_p,
		clock_timestamp(),
		nm_usuario_p,
		1,
		vl_item_w,
		null,
		ie_tipo_guia_w,
		cd_guia_p,
		ie_tipo_atend_tiss_w,
		null);

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
