CREATE OR REPLACE PROCEDURE insere_laudo_sistema_ant ( cd_pessoa_fisica_ant_p text, nm_usuario_p text, ds_laudo_nome_p text, ds_arquivo_pdf_p text) AS $body$
DECLARE

								
nr_seq_sequencia_w	bigint;
nr_seq_sequencia_detalhe_w	bigint;
existe_laudo bigint;

BEGIN
								
if (cd_pessoa_fisica_ant_p IS NOT NULL AND cd_pessoa_fisica_ant_p::text <> '') and (ds_laudo_nome_p IS NOT NULL AND ds_laudo_nome_p::text <> '') and (ds_arquivo_pdf_p IS NOT NULL AND ds_arquivo_pdf_p::text <> '')	then

    select coalesce(max(nr_sequencia),0) 
    into STRICT	nr_seq_sequencia_w
    from laudo_sis_antigo 
    where cd_sistema_ant = cd_pessoa_fisica_ant_p;
    
    if (nr_seq_sequencia_w <= 0) or (coalesce(nr_seq_sequencia_w::text, '') = '') then
      select 	nextval('laudo_sis_antigo_seq')
      into STRICT	nr_seq_sequencia_w
;

      insert into laudo_sis_antigo(nr_sequencia,
                                    dt_atualizacao,
                                    nm_usuario, 
                                    dt_atualizacao_nrec, 
                                    nm_usuario_nrec, 
                                    cd_sistema_ant)
                            values (nr_seq_sequencia_w,
                                   clock_timestamp(),
                                   nm_usuario_p,
                                   clock_timestamp(),
                                   nm_usuario_p,
                                   cd_pessoa_fisica_ant_p);									
    end if;
    

    select count(1) 
      into STRICT existe_laudo
      from laudo_sis_antigo a
     join laudo_sis_ant_detalhe b
       on a.nr_sequencia = b.nr_seq_lau_ant
    where a.cd_sistema_ant = cd_pessoa_fisica_ant_p
      and b.ds_nome = ds_laudo_nome_p;

    
    if (existe_laudo <= 0 ) then
      select 	nextval('laudo_sis_ant_detalhe_seq')
      into STRICT	nr_seq_sequencia_detalhe_w
;
      insert into laudo_sis_ant_detalhe(nr_sequencia,
                                         dt_atualizacao,
                                         nm_usuario,
                                         dt_atualizacao_nrec,
                                         nm_usuario_nrec,
                                         ds_nome,
                                         nr_seq_lau_ant,
                                         ds_arquivo)
                                 values (nr_seq_sequencia_detalhe_w,
                                         clock_timestamp(),
                                         nm_usuario_p,
                                         clock_timestamp(),
                                         nm_usuario_p,
                                         ds_laudo_nome_p,
                                         nr_seq_sequencia_w,
                                         ds_arquivo_pdf_p);

    end if;

  commit;
end if;
		
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
