CREATE OR REPLACE PROCEDURE insere_result_lab_pdf_bco_out ( nr_prescricao_p text, nr_seq_prescr_p text, nm_usuario_p text, nr_sequencia_p INOUT result_laboratorio_pdf.nr_sequencia%type, ds_erro_p INOUT text ) AS $body$
DECLARE


nr_seq_result_lab_w	bigint;
nr_seq_exame_w		bigint;
ie_formato_texto_w	smallint;


BEGIN

select 	MAX(nr_sequencia),
		MAX(coalesce(ie_formato_texto,1))
into STRICT 	nr_seq_result_lab_w,
		ie_formato_texto_w
from 	result_laboratorio
where	nr_prescricao	= nr_prescricao_p
and		nr_seq_prescricao = nr_seq_prescr_p;

select 	MAX(nr_seq_exame)
into STRICT	nr_seq_exame_w
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia = nr_seq_prescr_p;

if (coalesce(nr_seq_result_lab_w::text, '') = '') then
	insert into result_laboratorio(
		nr_sequencia,
		ds_resultado,
		nr_prescricao,
		nr_seq_prescricao,
		nm_usuario,
		dt_atualizacao,
		ie_formato_texto,
		ie_cobranca)
	values (nextval('result_laboratorio_seq'),
		WHEB_MENSAGEM_PCK.get_texto(280689,null),
		nr_prescricao_p,
		nr_seq_prescr_p,
		nm_usuario_p,
		clock_timestamp(),
		3,
		'N');

end if;

select 	MAX(nr_sequencia),
		MAX(coalesce(ie_formato_texto,1))
into STRICT 	nr_seq_result_lab_w,
		ie_formato_texto_w
from 	result_laboratorio
where	nr_prescricao	= nr_prescricao_p
and		nr_seq_prescricao = nr_seq_prescr_p;


if (nr_seq_result_lab_w IS NOT NULL AND nr_seq_result_lab_w::text <> '') then
	begin

	if (ie_formato_texto_w <> 3) then
		update 	result_laboratorio
		set 	ie_formato_texto = 3,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
		where 	nr_prescricao = nr_prescricao_p
		and		nr_seq_prescricao = nr_seq_prescr_p;

		commit;

	end if;

	select	nextval('result_laboratorio_pdf_seq')

	into STRICT	nr_sequencia_p

	;


	insert into result_laboratorio_pdf(
			nr_sequencia,
			nr_prescricao,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_prescricao,
			nr_seq_resultado,
			nr_seq_exame,
			ds_pdf_serial
			)
	values (
			nr_sequencia_p,
			nr_prescricao_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_prescr_p,
			nr_seq_result_lab_w,
			nr_seq_exame_w,
			' ');

	exception
		when others then
			ds_erro_p := SQLERRM(SQLSTATE);
		end;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
