CREATE OR REPLACE PROCEDURE insere_submotivo_alta_med ( nr_atendimento_p bigint, cd_submotivo_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE

nr_sequencia_w	bigint;


BEGIN

	select	nextval('atendimento_alta_medica_seq')
	into STRICT 	nr_sequencia_w
	;

	if (coalesce(nr_atendimento_p,0) > 0) and (coalesce(cd_submotivo_p,0) > 0) and (ie_opcao_p  = 'G') then

		insert into atendimento_alta_medica(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_atendimento,
				cd_submotivo_alta_med)
		values (
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p ,
				nr_atendimento_p,
				cd_submotivo_p);

	elsif (coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p  = 'D') then

		delete 	FROM atendimento_alta_medica
		where	nr_atendimento = nr_atendimento_p;

	end if;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
