CREATE OR REPLACE PROCEDURE insere_w_integracao_eritel ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nr_atendimento_p bigint, ie_estornar_limpeza_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_ramal_w	integer;
ie_pac_isolamento_w	varchar(1);

BEGIN
if (coalesce(ie_estornar_limpeza_p,'N') <> 'S') then
	Select	coalesce(max(ie_paciente_isolado),'N')
	into STRICT	ie_pac_isolamento_w
	from	atendimento_paciente
	where	nr_Atendimento = nr_atendimento_p;
end if;

nr_ramal_w := Obter_ramal_unidade_atend(cd_setor_atendimento_p,cd_unidade_basica_p,cd_unidade_compl_p);

if (nr_ramal_w IS NOT NULL AND nr_ramal_w::text <> '') and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') and (cd_unidade_basica_p IS NOT NULL AND cd_unidade_basica_p::text <> '') and (cd_unidade_compl_p IS NOT NULL AND cd_unidade_compl_p::text <> '')  then
	insert	into W_integracao_eritel(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_ramal,
			ie_integrado,
			cd_unidade_basica,
			cd_unidade_compl,
			cd_setor_atendimento,
			ie_status_eritel,
			cd_estabelecimento)
		values (nextval('w_integracao_eritel_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_ramal_w,
			'N',
			cd_unidade_basica_p,
			cd_unidade_compl_p,
			cd_setor_atendimento_p,
			CASE WHEN ie_estornar_limpeza_p='S' THEN  20  ELSE CASE WHEN ie_pac_isolamento_w='S' THEN  12  ELSE 11 END  END ,
			cd_estabelecimento_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
