CREATE OR REPLACE PROCEDURE inserir_acordo_ava_rh ( nr_seq_ordem_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);


BEGIN
select	max(a.cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	usuario a
where	a.nm_usuario = nm_usuario_p;

if	((cd_pessoa_fisica_w = sis_obter_gerente(nm_usuario_p, 'C')) or (cd_pessoa_fisica_w = sis_obter_diretor(nm_usuario_p, 'C'))) then
begin
	update	man_pesquisa_rh
	set	dt_acordo_superior = clock_timestamp(),
		nm_acordo_superior = nm_usuario_p
	where	nr_seq_ordem = nr_seq_ordem_p;
end;
else
begin
	update	man_pesquisa_rh
	set	dt_acordo_treinado = clock_timestamp(),
		nm_acordo_treinado = nm_usuario_p
	where	nr_seq_ordem = nr_seq_ordem_p;
end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
