SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_adendo_laudo ( nr_seq_laudo_p bigint, nr_atendimento_p bigint, nm_usuario_p text, nr_seq_laudo_q INOUT bigint) AS $body$
DECLARE


nr_laudo_w		bigint;
nr_seq_laudo_w		bigint;


BEGIN

select coalesce(max(nr_laudo),0)+1
into STRICT	nr_laudo_w
from laudo_paciente
 where nr_atendimento = nr_atendimento_p;

 select	nextval('laudo_paciente_seq')
 into STRICT	nr_seq_laudo_w
;

insert into laudo_paciente(	nr_sequencia,
				nr_atendimento,
				dt_entrada_unidade,
				nr_laudo,
				nm_usuario,
				dt_atualizacao,
				cd_medico_resp,
				ds_titulo_laudo,
				dt_laudo,
				cd_laudo_padrao,
				ie_normal,
				dt_exame,
				nr_prescricao,
				cd_protocolo,
				nr_seq_proc,
				nr_seq_prescricao,
				nr_controle,
				cd_setor_atendimento,
				nr_exame,
				cd_medico_aux,
				dt_envelopado,
				dt_inicio_digitacao,
				nm_medico_solicitante,
				cd_setor_usuario,
				cd_pessoa_fisica,
				cd_anestesista,
				qt_imagem,
				nr_seq_superior)
SELECT				nr_seq_laudo_w,
				nr_atendimento,
				dt_entrada_unidade,
				nr_laudo_w,
				nm_usuario_p,
				clock_timestamp(),
				cd_medico_resp,
				substr('Adendo '||ds_titulo_laudo,1,255),
				clock_timestamp(),
				cd_laudo_padrao,
				ie_normal,
				dt_exame,
				nr_prescricao,
				cd_protocolo,
				nr_seq_proc,
				nr_seq_prescricao,
				nr_controle,
				cd_setor_atendimento,
				nr_exame,
				cd_medico_aux,
				dt_envelopado,
				clock_timestamp(),
				nm_medico_solicitante,
				cd_setor_usuario,
				cd_pessoa_fisica,
				cd_anestesista,
				qt_imagem,
				nr_seq_laudo_p
from				laudo_paciente
where				nr_sequencia = nr_seq_laudo_p;

CALL Vincular_Procedimento_Laudo(nr_seq_laudo_w,'N',nm_usuario_p);
nr_seq_laudo_q := nr_seq_laudo_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
