CREATE OR REPLACE PROCEDURE inserir_agenda_build (nm_usuario_p text, dt_agendamento_p timestamp, cd_versao_p text, ie_aplicacao_p text, ie_periodo_p text, nr_seq_ordem_serv_p bigint, ds_motivo_agendamento_p text, ds_hotfix_p text default null) AS $body$
DECLARE


ds_assunto_w varchar(4000);
ds_mensagem_w varchar(4000);
ds_email_origem_w varchar(4000);
ds_email_destino_w varchar(4000);
  i RECORD;

BEGIN

insert into agendamento_build(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_agendamento,
			cd_versao,
			ie_aplicacao,
			ie_periodo,
			nr_seq_ordem_serv,
			ie_status,
			dt_liberacao,
			ds_motivo_agendamento,
      ds_hotfix)
	values (nextval('agendamento_build_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			dt_agendamento_P,
			cd_versao_p,
			ie_aplicacao_p,
			ie_periodo_p,
			nr_seq_ordem_serv_p,
			'BA',
			clock_timestamp(),
			ds_motivo_agendamento_p,
      ds_hotfix_p);
commit;

ds_assunto_w := 'Novo Build da versao '|| cd_versao_p || ' foi agendado';
ds_mensagem_w := 'Um novo build para a aplicacao HTML5, versao '|| cd_versao_p ||
' foi agendado para o dia '|| dt_agendamento_p ||' antecedendo seu pedido. Verifique se haverao alteracoes importantes para que seu agendamento seja mantido.';
ds_email_origem_w := 'support.informatics@philips.com';

for i in (
SELECT distinct a.cd_versao, a.dt_agendamento, a.ie_aplicacao, a.dt_atualizacao, u.cd_pessoa_fisica, a.nm_usuario
from agendamento_build a, usuario u
where u.nm_usuario = a.nm_usuario
and cd_versao_p = a.cd_versao
and ie_aplicacao_p = a.ie_aplicacao
and dt_agendamento_p < a.dt_agendamento )
Loop

  ds_email_destino_w := obter_email_pf(i.cd_pessoa_fisica);
  CALL enviar_email(ds_assunto_w, ds_mensagem_w, ds_email_origem_w, ds_email_destino_w, 'Tasy', null);

end Loop;
end;
$body$
