CREATE OR REPLACE PROCEDURE inserir_ajuste_base_cli_del (nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;
nr_seq_ajuste_base_w	varchar(10);
ds_consulta_w   	varchar(512);
nm_responsavel_w	varchar(60);
ie_fase_w		varchar(1);
ds_motivo_w             varchar(2000);
ds_comando_w            text;
c02              integer;
retorno_w        integer;


BEGIN

begin
	EXECUTE 'drop table ajuste_versao_base_w';
exception
when others then
	qt_registros_w:= 0;
end;

EXECUTE	'create table ajuste_versao_base_w ' ||
			' as ' ||
			'	(select	a.ie_fase, ' ||
			' 		a.nr_seq_ajuste_base, ' ||
			' 		a.nm_responsavel, ' ||
			' 		a.ds_motivo, ' ||
			'		to_lob(a.ds_comando) ds_comando ' ||
			' 	from	ajuste_versao_base_longo a ) ';

delete from ajuste_versao_base_longo;
commit;

ds_consulta_w := ' select a.ie_fase, '    ||
			' a.nr_seq_ajuste_base, ' ||
			' a.nm_responsavel, ' ||
			' a.ds_motivo, ' ||
			' a.ds_comando ' ||
		' from ajuste_versao_base_w a ';

C02 := DBMS_SQL.OPEN_CURSOR;
DBMS_SQL.PARSE(C02, ds_consulta_w, dbms_sql.Native);
DBMS_SQL.DEFINE_COLUMN(C02,1,ie_fase_w,1);
