CREATE OR REPLACE PROCEDURE inserir_alerta_atend_alta ( nr_atendimento_p bigint, nm_usuario_p text, nr_seq_tipo_alerta_p bigint, cd_motivo_alta_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_alerta_w		varchar(255);
nr_seq_alerta_w		bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then

	select	nextval('atendimento_alerta_seq')
	into STRICT	nr_sequencia_w
	;

	select 	ds_alerta, nr_seq_alerta
	into STRICT 	ds_alerta_w, nr_seq_alerta_w
	from 	MOTIVO_ALTA_ALERTA
	where 	nr_seq_tipo_alerta = nr_seq_tipo_alerta_p
	and	cd_motivo_alta = cd_motivo_alta_p;

	insert into atendimento_alerta(
		nr_sequencia,
		nr_atendimento,
		dt_alerta,
		dt_atualizacao,
		nm_usuario,
		ds_alerta,
		ie_situacao,
		dt_fim_alerta,
		dt_liberacao,
		dt_inativacao,
		nm_usuario_inativacao,
		ds_justificativa,
		nr_seq_tipo_alerta,
		nr_seq_alerta)
	values (
		nr_sequencia_w,
		nr_atendimento_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		ds_alerta_w,
		'A',
		null,
		clock_timestamp(),
		null,
		null,
		null,
		nr_seq_tipo_alerta_p,
		nr_seq_alerta_w);

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
