SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_alerta_atend ( nr_atendimento_p bigint, ds_alerta_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_tipo_alerta_w	bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then

	select	nextval('atendimento_alerta_seq')
	into STRICT	nr_sequencia_w
	;

	select	max(nr_sequencia)
	into STRICT	nr_seq_tipo_alerta_w
	from	tipo_alerta_atend
	where	coalesce(ie_isolamento,'N')	= 'S';

	insert into atendimento_alerta(
		nr_sequencia,
		nr_atendimento,
		dt_alerta,
		dt_atualizacao,
		nm_usuario,
		ds_alerta,
		ie_situacao,
		dt_fim_alerta,
		dt_liberacao,
		dt_inativacao,
		nm_usuario_inativacao,
		ds_justificativa,
		nr_seq_tipo_alerta)
	values (
		nr_sequencia_w,
		nr_atendimento_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		ds_alerta_p,
		'A',
		null,
		clock_timestamp(),
		null,
		null,
		null,
		nr_seq_tipo_alerta_w);

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
