CREATE OR REPLACE PROCEDURE inserir_alerta_texto_padrao ( nr_seq_alerta_p bigint, nr_seq_texto_padrao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_texto_padrao_w	varchar(2000);


BEGIN

if (coalesce(nr_seq_alerta_p,0) <> 0) then

	select	max(ds_texto_padrao)
	into STRICT	ds_texto_padrao_w
	from	alerta_texto_padrao
	where	nr_sequencia = nr_seq_texto_padrao_p;

	if (ds_texto_padrao_w IS NOT NULL AND ds_texto_padrao_w::text <> '') then

		update	alerta_paciente
		set	ds_alerta = ds_texto_padrao_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_alerta_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
